<?php
require_once(dirname(__FILE__) . "/email.class.php");class email_record_table_interface extends Email{	protected $log_class;	public function __construct($category = 0, $log_class = "bushido/email_log") {		$this->log_class = $log_class;		parent::__construct($category);	}	public function log_email($to, $subject, $body, $headers) {		// Log Email in DB		require_once($this->log_class . ".class.php");		$email_log_class = new $this->log_class();		$email_log_class->set_data_from_email_class(array(			"to" => $to,			"subject" => $subject,			"body" => $body,			"from" => $this->get_from($headers),			"category" => $this->category));		if ($email_log_class->save()) {			return $email_log_class->get_id();		} else {			$this->error('Problem encountered logging email in database');			return false;		}	}	}?>